PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "products" ("id" integer primary key autoincrement not null, "name" varchar not null, "slug" varchar not null, "description" text not null, "category" varchar not null, "subcategory" varchar, "base_price" numeric not null, "pricing_options" text, "specifications" text, "design_templates" text, "image" varchar, "is_active" tinyint(1) not null default '1', "sort_order" integer not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "customers" ("id" integer primary key autoincrement not null, "first_name" varchar not null, "last_name" varchar not null, "email" varchar not null, "phone" varchar, "company" varchar, "abn" varchar, "address" text, "suburb" varchar, "state" varchar, "postcode" varchar, "country" varchar not null default 'Australia', "notes" text, "is_active" tinyint(1) not null default '1', "email_verified_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "order_items" ("id" integer primary key autoincrement not null, "order_id" integer not null, "product_id" integer not null, "product_name" varchar not null, "quantity" integer not null, "unit_price" numeric not null, "total_price" numeric not null, "options" text, "design_notes" text, "design_file" varchar, "proof_status" varchar not null default 'pending', "proof_notes" text, "created_at" datetime, "updated_at" datetime, foreign key("order_id") references "orders"("id") on delete cascade, foreign key("product_id") references "products"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "orders" ("id" integer primary key autoincrement not null, "order_number" varchar not null, "customer_id" integer not null, "status" varchar not null default 'pending', "subtotal" numeric not null, "tax" numeric not null default '0', "shipping" numeric not null default '0', "total" numeric not null, "payment_status" varchar not null default 'pending', "payment_method" varchar, "shipping_address" text, "shipping_method" varchar, "expected_delivery" date, "special_instructions" text, "design_files" text, "proof_approval" text, "paid_at" datetime, "shipped_at" datetime, "delivered_at" datetime, "created_at" datetime, "updated_at" datetime, foreign key("customer_id") references "customers"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "quote_requests" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "phone" varchar not null, "service" varchar not null, "description" text, "quantity" varchar, "urgency" varchar check ("urgency" in ('standard', 'urgent', 'express')) not null default 'standard', "status" varchar check ("status" in ('new', 'reviewing', 'quoted', 'accepted', 'rejected')) not null default 'new', "notes" text, "created_at" datetime, "updated_at" datetime, "service_category" varchar, "size" varchar, "artwork_file" varchar, "delivery_address" text, "special_requirements" text);
CREATE TABLE IF NOT EXISTS "contact_messages" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "message" text not null, "status" varchar check ("status" in ('new', 'read', 'replied', 'archived')) not null default 'new', "notes" text, "created_at" datetime, "updated_at" datetime);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "products_slug_unique" on "products" ("slug");
CREATE UNIQUE INDEX "customers_email_unique" on "customers" ("email");
CREATE UNIQUE INDEX "orders_order_number_unique" on "orders" ("order_number");
COMMIT;
